CREATE TABLE "companies" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "number" varchar(255), "number_tax" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);CREATE TABLE "companies_people" ("company_id" integer, "person_id" integer);CREATE TABLE "contacts" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "contactable_id" integer, "contactable_type" varchar(255), "contact_type" varchar(255), "value" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);CREATE TABLE "people" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "first_name" varchar(255), "second_name" varchar(255), "last_name" varchar(255), "title_prefix" varchar(255), "title_suffix" varchar(255), "sex" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);CREATE TABLE "sessions" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "session_id" varchar(255) NOT NULL, "data" text, "created_at" datetime, "updated_at" datetime);